export default function ThemeItem({ theme, active, onClick }) {
    return (
      <span
        onClick={onClick}
        style={{
          cursor: "pointer",
          paddingLeft: 8,
          fontWeight: active ? "bold" : "normal",
        }}
      >
        <span style={{ color: theme.primaryColor }}>Primary</span> /
        <span style={{ color: theme.secondaryColor }}>Secondary</span>
      </span>
    );
  }
